import React, { useState } from "react";
import "./styles.css";
import data from "./demo-data.json";
import Wrapper from "./components/Wrapper";

export default function App() {
  const [input, setInput] = useState('')
  const [resp, setRespt] = useState(null)

  console.log(input)
  console.log(resp)

  const handleClick = (path: (string | number)[]) => {
    let res = data
    let parsedPath = 'res'

    path.forEach((field: string | number) => {
      res = res[field]
      parsedPath = (typeof field === 'string' ? `${parsedPath}.${field}` : `${parsedPath}[${field}]`)
    })
    setInput(parsedPath)
    setRespt(res)
  }

  const iterateObject = (object: any, path?: (string| number)[]) => {
    return Object.keys(object).map((key) => {
      let objectPath: (string| number)[] = path ? [...path, key] : []
      let onClick = () => handleClick(objectPath)
      let clickable = true
      let content

      switch (typeof object[key]) {
        case "string":
          content = `"${object[key]}"`
          break
        case "boolean":
          content = object[key].toString()
          break
        case "object":     
          if (Array.isArray(object[key])) {
            clickable = false
            onClick = () => {}

            content = <>&#91;
            {object[key].map((elem: any, index: number) => <>&#123;{iterateObject(elem, [...objectPath, index])}&#125;</>)}
            &#93;
            </>
            break
          }
        default:
            content = String(object[key])//.toString()
            break
      }

      return (
        <Wrapper key={key} property={key} onClick={onClick} clickable={clickable}>
          {content}
        </Wrapper>)
    });
  };

  return (
    <div className="App">
      <div>
        <label htmlFor="property-input">Property</label>
        <input type="text" id="property-input" />
      </div>

      <div>
        Response
        <div>{iterateObject(data)}</div>
      </div>
    </div>
  );
}
