import React, { useState } from "react";
import "./styles.css";
import data from "./demo-data.json";
import Wrapper from "./components/Wrapper";

export default function App() {
  const [input, setInput] = useState('')
  const [resp, setRespt] = useState(null)

  console.log(input)
  console.log(resp)

  const handleClick = (path: string[]) => {
    let res = data
    let parsedPath = 'res'

    path.forEach((field: string | number) => {
      res = res[field]
      parsedPath = (typeof field === 'string' ? `${parsedPath}.${field}` : `${parsedPath}[${field}]`)
    })
    setInput(parsedPath)
    setRespt(res)
  }

  const iterateObject = (object: any, path = []) => {

    return Object.keys(object).map((key) => {
      let objectPath = [...path, key]
      let onClick = () => handleClick(objectPath)
      let clickable = true
      let content

      console.log('typeof object', typeof object[key])
      switch (typeof object[key]) {

        case "string":
          content = `"${object[key]}"`
          break
        case "boolean":
          content = object[key].toString()
          break
        default:
          clickable = false
          onClick = () => {}
          if (Array.isArray(object[key])) {
            content = `[
                ${object[key].map((elem: any, index: number) => {
                  console.log('elem', elem)
              // @ts-ignore
              return <>&#123;{iterateObject(elem, [...objectPath, index])}&#125;</>
            })}
                ]`
            break
          } else {
                          // @ts-ignore
            content = `{${iterateObject(object[key], objectPath)}}`
            break
          }
      }

      return (
        <Wrapper key={key} property={key} onClick={onClick} clickable={clickable}>
          {content}
        </Wrapper>)
    });
  };

  return (
    <div className="App">
      <div>
        <label htmlFor="property-input">Property</label>
        <input type="text" id="property-input" />
      </div>

      <div>
        Response
        <div>{iterateObject(data)}</div>
      </div>
    </div>
  );
}
