import React, { useState } from "react";
import "./styles.css";
import demodata from "./demo-data.json";
import PropertyWrapper from "./components/PropertyWrapper";

export default function App() {
  const [input, setInput] = useState('')
  const [resp, setRespt] = useState(null)


  const handleClick = (path: (string | number)[]) => {
    let res = demodata
    let parsedPath = 'res'

    path.forEach((field: string | number) => {
      res = res[field]
      parsedPath = (typeof field === 'string' ? `${parsedPath}.${field}` : `${parsedPath}[${field}]`)
    })

    setInput(parsedPath)
    // @ts-ignore
    setRespt(String(res))
  }

  const getContent = (data: any, key: string, path?: (string| number)[]) => {
    const objectPath: (string| number)[] = path ? [...path, key] : [key]
    let onClick = () => handleClick(objectPath)
    let clickable = true
    let content

    switch (typeof data[key]) {
      case "string":
        content = `'${data[key]}'`
        break
      case "object":     
        if (Array.isArray(data[key])) {
          clickable = false
          onClick = () => {}

          content = <>&#91;
          {data[key].map((elem: any, index: number) => <div key={`${objectPath}-${index}`}>&#123;<JSONObject data={elem} path={[...objectPath, index]} />&#125;</div>)}
          &#93;
          </>
          break
        }
      default:
          content = String(data[key])
          break
    }

    return { content, onClick, clickable}
  }

  const JSONObject = (data: any, path?: (string| number)[]) => {
    return Object.keys(data).map((key) => {
      const { content, onClick, clickable} = getContent(data, key, path)

      return (
        <PropertyWrapper key={key} property={key} onClick={onClick} clickable={clickable}>
          {content}
        </PropertyWrapper>)
    });
  };


  return (
    <div className="App">
      <div>
        <label htmlFor="property-input" className="label">Property</label>
        <input type="text" id="property-input" value={input} className="monospace text-box"/>
      </div>
      <div className="monospace response">
        {resp}
      </div>
      <div>
        <div className="label">Response</div>
        {/* <div className="monospace text-box">{iterateObject(data)}</div> */}
        <div className="monospace text-box">
          <JSONObject data={demodata} />
        </div>

      </div>
    </div>
  );
}
