import "./styles.css";
import data from "./demo-data.json";
import React, { useState } from "react";

export default function App() {
  const [input, setInput] = useState('')
  const [resp, setRespt] = useState(null)

  console.log(input)
  console.log(resp)

  const Wrapper = ({ path, property, children }: { path?: string[], property: string, children: React.ReactNode }) => {
    const handleClick = (path: string[]) => {
      let res = data
      let parsedPath = 'res'

      path.forEach((field: string | number) => {
        res = res[field]
        parsedPath = (typeof field === 'string' ? `${parsedPath}.${field}` : `${parsedPath}[${field}]`)
      })
      setInput(parsedPath)
      setRespt(res)
    }

    return <div className="indented">
      <span className={path ? "clickable-property key" : "key"}
      onClick={() => path ? handleClick(path) : {}}
      >{`${property}: `}</span>
      {children},
    </div>
  }

  const iterateObject = (object: any, path = []) => {
    return Object.keys(object).map((key) => {
      let objectPath = [...path, key]

      switch (typeof object[key]) {
        case "string":
        return (
            <Wrapper key={key} path={objectPath} property={key}>
              {`"${object[key]}"`}
            </Wrapper>)
        case "boolean":
          return (
            <Wrapper key={key} path={objectPath} property={key}>
              {object[key].toString()}
            </Wrapper>)
        default:
          if (Array.isArray(object[key])) {
            return (
              <Wrapper key={key} property={key}>
                [
                {object[key].map((elem: any, index: number) => {
                  // @ts-ignore
                  return <>&#123;{iterateObject(elem, [...objectPath, index])}&#125;</>
                })}
                ]
              </Wrapper>
            )
          } else {
            return (
              <Wrapper key={key} property={key}>
                &#123;
                {/* @ts-ignore */}
                {iterateObject(object[key], objectPath)}
                &#125;
              </Wrapper>
            )

          }
      }
    });
  };

  return (
    <div className="App">
      <div>
        <label htmlFor="property-input">Property</label>
        <input type="text" id="property-input" />
      </div>

      <div>
        Response
        <div>{iterateObject(data)}</div>
      </div>
    </div>
  );
}
